$(document).ready(e => {
    var o = $("#meta-mobile-sticky-top"), n = $(".annonce-title");
    n[0] && (o.hide(), $target = n.offset().top + n.height(), $(document).on("scroll", e => {
        $(this).scrollTop() >= $target ? o.slideDown() : o.hide()
    }));
    var t = $("#filtre");
    if (t[0]) {
        window.screenX > 992 ? t.removeClass("modal") : t.addClass("modal"), $(window).resize(function () {
            window.screenX > 992 ? t.removeClass("modal") : t.addClass("modal")
        });
        var l = document.getElementById("showFilter"), d = document.getElementById("closeFilter");
        l.onclick = function () {
            t.fadeIn(200)
        }, d.onclick = function () {
            t.hide(200)
        }
    }
    var statutBtn = $('#statusBtn')
    var statutState = $('#statutInputState')
    window.$statutState = false;
    statutBtn.attr('disabled', true);
    $('#alert-status').hide(200)

    statutState.on('change', () => {
        if (statutState.val() == 1) {
            $('#alert-status').hide(200)
            window.$statutState = true;
        } else {
            $('#alert-status').show(200)
            statutBtn.attr('disabled', true);
            window.$statutState = false;
        }
    })

    if (window.$statutState) {
        window.$validator = $("#formAnnonce").validate({
            rules: {
                "societe": {
                    "required": true,
                },
                "email": {
                    "email": true,
                },
                "tel": {
                    "required": true
                }
            }
        })
    }
});
